import { useEffect } from 'react';

/**
 * Preloads images when a section becomes visible
 * @param {Array} imageUrls - Array of image URLs to preload
 * @param {boolean} isVisible - Whether the section is visible
 */
export const useImagePreloader = (imageUrls, isVisible) => {
  useEffect(() => {
    if (!isVisible || !imageUrls || imageUrls.length === 0) return;

    // Preload images when section becomes visible
    imageUrls.forEach((url) => {
      if (!url) return;
      
      const link = document.createElement('link');
      link.rel = 'prefetch';
      link.as = 'image';
      link.href = url;
      document.head.appendChild(link);

      // Also create an Image object to trigger browser caching
      const img = new Image();
      img.src = url;
    });
  }, [isVisible, imageUrls]);
};
