import React, { useState, useRef, useEffect } from 'react';

const OptimizedImage = ({ 
  src, 
  alt, 
  className = '', 
  style = {},
  eager = false,
  onLoad,
  ...props 
}) => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [shouldLoad, setShouldLoad] = useState(eager);
  const imgRef = useRef(null);
  const observerRef = useRef(null);

  useEffect(() => {
    // If eager, load immediately
    if (eager) {
      setShouldLoad(true);
      return;
    }

    // Use Intersection Observer for lazy loading with early trigger
    if (!imgRef.current) return;

    observerRef.current = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setShouldLoad(true);
            // Disconnect observer once we start loading
            if (observerRef.current && imgRef.current) {
              observerRef.current.unobserve(imgRef.current);
            }
          }
        });
      },
      {
        rootMargin: '300px', // Start loading 300px before entering viewport
        threshold: 0.01
      }
    );

    observerRef.current.observe(imgRef.current);

    return () => {
      if (observerRef.current && imgRef.current) {
        observerRef.current.unobserve(imgRef.current);
      }
    };
  }, [eager]);

  const handleLoad = () => {
    setIsLoaded(true);
    if (onLoad) onLoad();
  };

  return (
    <div ref={imgRef} className="relative" style={{ width: '100%', height: '100%' }}>
      {!isLoaded && (
        <div className="absolute inset-0 bg-gray-200 animate-pulse" style={{ zIndex: 1 }} />
      )}
      {shouldLoad && (
        <img
          src={src}
          alt={alt}
          className={`${className} transition-opacity duration-200 ${
            isLoaded ? 'opacity-100' : 'opacity-0'
          }`}
          style={{ ...style, zIndex: 2, position: 'relative' }}
          loading={eager ? 'eager' : 'lazy'}
          decoding="async"
          fetchpriority={eager ? 'high' : 'auto'}
          onLoad={handleLoad}
          {...props}
        />
      )}
    </div>
  );
};

export default OptimizedImage;
