import React, { useEffect, useState } from 'react';
import { ArrowRight, Waves } from 'lucide-react';
import { Button } from './ui/button';

const HeroSection = () => {
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      const headerOffset = 80;
      const elementPosition = element.getBoundingClientRect().top;
      const offsetPosition = elementPosition + window.pageYOffset - headerOffset;

      window.scrollTo({
        top: offsetPosition,
        behavior: 'smooth'
      });
    }
  };

  return (
    <section id="hero" className="relative min-h-screen flex items-center justify-center pt-20 overflow-hidden">
      <div className="absolute inset-0 z-0">
        <div
          className="absolute inset-0 hero-background"
          style={
            {
              backgroundImage: 'url(https://images.unsplash.com/photo-1541742425281-c1d3fc8aff96?crop=entropy&cs=srgb&fm=jpg&ixid=M3w3NDQ2Mzl8MHwxfHNlYXJjaHwxfHxmaXNoaW5nfGVufDB8fHx8MTc2MjI0NzE3NHww&ixlib=rb-4.1.0&q=85)',
              backgroundSize: 'cover',
              backgroundPosition: 'center',
              backgroundAttachment: 'scroll',
              animation: 'bannerZoom 20s ease-in-out infinite alternate'
            }
          }
        >
          <div className="absolute inset-0 bg-gradient-to-br from-[#003366]/85 via-[#003366]/75 to-[#003366]/90"></div>
          <div 
            className="absolute inset-0 opacity-30"
            style={{
              backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.03'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`
            }}
          ></div>
        </div>
      </div>

      <div className="absolute bottom-0 left-0 right-0 h-32 z-10 overflow-hidden">
        <svg className="waves" xmlns="http://www.w3.org/2000/svg" viewBox="0 24 150 28" preserveAspectRatio="none" shapeRendering="auto">
          <defs>
            <path id="gentle-wave" d="M-160 44c30 0 58-18 88-18s 58 18 88 18 58-18 88-18 58 18 88 18 v44h-352z" />
          </defs>
          <g className="parallax">
            <use href="#gentle-wave" x="48" y="0" fill="rgba(255,255,255,0.7)" />
            <use href="#gentle-wave" x="48" y="3" fill="rgba(255,255,255,0.5)" />
            <use href="#gentle-wave" x="48" y="5" fill="rgba(255,255,255,0.3)" />
            <use href="#gentle-wave" x="48" y="7" fill="#fff" />
          </g>
        </svg>
      </div>

      <div className="relative z-20 w-full max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white/10 backdrop-blur-md rounded-3xl border border-white/20 p-8 md:p-12 shadow-2xl max-w-5xl mx-auto">
          <div className="text-center">
            <div className="inline-block mb-6 animate-fade-in-up">
              <span className="inline-block px-4 py-2 bg-[#FF6600]/30 backdrop-blur-sm rounded-full text-[#FF6600] font-semibold text-sm border border-[#FF6600]/40 shadow-lg">
                Fresh from the Arabian Sea
              </span>
            </div>

            <h1 className="text-5xl md:text-7xl font-bold text-white mb-6 leading-tight animate-slide-up drop-shadow-lg">
              Tradition Meets <span className="text-[#FF6600] inline-block animate-pulse-slow">Taste</span>
            </h1>

            <div className="w-32 h-1 bg-gradient-to-r from-transparent via-[#FF6600] to-transparent mx-auto mb-8 animate-expand"></div>

            <p className="text-xl md:text-2xl text-white mb-10 max-w-3xl mx-auto leading-relaxed animate-fade-in-up-delay drop-shadow-md">
              Delivering the freshest catch from shore to your door
            </p>

            <div className="flex flex-col sm:flex-row gap-4 justify-center items-center animate-fade-in-up-delay-2">
              <Button
                onClick={() => scrollToSection('varieties')}
                className="bg-gradient-to-r from-[#FF6600] to-[#FF8533] hover:from-[#FF6600]/90 hover:to-[#FF8533]/90 text-white px-8 py-6 text-lg transition-all duration-300 hover:scale-110 shadow-xl hover:shadow-2xl transform hover:-translate-y-1"
                size="lg"
              >
                Explore Varieties
                <ArrowRight className="ml-2 animate-bounce-x" size={20} />
              </Button>
              <Button
                onClick={() => scrollToSection('contact')}
                className="bg-white/10 backdrop-blur-md hover:bg-white/20 text-white border-2 border-white/30 hover:border-white/50 px-8 py-6 text-lg transition-all duration-300 hover:scale-110 shadow-xl hover:shadow-2xl transform hover:-translate-y-1"
                size="lg"
              >
                Contact Us
              </Button>
            </div>
          </div>

          <div className="mt-12">
            <div className="inline-flex items-center space-x-2 text-white animate-bounce">
              <Waves size={24} className="animate-wave" />
              <span className="text-sm drop-shadow-md">Scroll to discover</span>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
