import React, { useEffect, useRef, useState } from 'react';
import { Anchor, Ship, Award, Users } from 'lucide-react';

const AboutSection = () => {
  const [isVisible, setIsVisible] = useState(false);
  const sectionRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.01, rootMargin: '50px' }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => {
      if (sectionRef.current) {
        observer.unobserve(sectionRef.current);
      }
    };
  }, []);

  const features = [
    {
      icon: Anchor,
      title: 'Traditional Heritage',
      description: 'Rooted in generations of Koli fishing tradition'
    },
    {
      icon: Ship,
      title: 'Daily Fresh Catch',
      description: 'Fish caught and delivered the same day'
    },
    {
      icon: Award,
      title: 'Premium Quality',
      description: 'Highest standards of freshness and hygiene'
    },
    {
      icon: Users,
      title: 'Trusted by Thousands',
      description: 'Serving homes and businesses across the city'
    }
  ];

  return (
    <section id="about" ref={sectionRef} className="py-20 bg-gradient-to-b from-white via-[#E8F4F8] to-white relative overflow-hidden">
      {/* Decorative background elements */}
      <div className="absolute top-0 left-0 w-72 h-72 bg-[#FF6600]/5 rounded-full blur-3xl -translate-x-1/2 -translate-y-1/2"></div>
      <div className="absolute bottom-0 right-0 w-96 h-96 bg-[#003366]/5 rounded-full blur-3xl translate-x-1/2 translate-y-1/2"></div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
        {/* Header Section */}
        <div className={`text-center mb-16 transition-all duration-500 transform ${
          isVisible ? 'translate-y-0 opacity-100 scale-100' : 'translate-y-10 opacity-0 scale-95'
        }`}>
          <div className="inline-block mb-4">
            <span className="inline-block px-4 py-2 bg-[#FF6600]/10 backdrop-blur-sm rounded-full text-[#FF6600] font-semibold text-sm border border-[#FF6600]/20 animate-fade-in-up">
              About Koli Catch
            </span>
          </div>
          <h2 className="text-4xl md:text-5xl font-bold text-[#003366] mb-6 animate-slide-up">
            Our <span className="text-[#FF6600] relative">
              Story
              <span className="absolute bottom-0 left-0 right-0 h-1 bg-gradient-to-r from-transparent via-[#FF6600] to-transparent animate-expand"></span>
            </span>
          </h2>
          <div className="w-32 h-1 bg-gradient-to-r from-transparent via-[#FF6600] to-transparent mx-auto mb-8 animate-expand"></div>
          <p className="text-lg text-gray-700 max-w-3xl mx-auto leading-relaxed animate-fade-in-up-delay">
            For generations, the Koli community has been the heartbeat of Mumbai's fishing industry. 
            At Koli Catch, we honor this rich heritage by combining traditional fishing wisdom with 
            modern quality standards. Every fish we deliver carries the legacy of our ancestors and 
            the promise of unmatched freshness.
          </p>
        </div>

        {/* Features Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 mb-16">
          {features.map((feature, index) => {
            const Icon = feature.icon;
            return (
              <div
                key={index}
                className={`group relative bg-white rounded-2xl p-8 shadow-lg hover:shadow-2xl transition-all duration-500 transform hover:-translate-y-3 hover:scale-105 ${
                  isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'
                }`}
                style={{ 
                  transitionDelay: `${index * 100}ms`,
                  animation: isVisible ? `featureCardSlide 0.6s ease-out ${index * 0.1}s both` : 'none'
                }}
              >
                {/* Hover glow effect */}
                <div className="absolute inset-0 bg-gradient-to-br from-[#FF6600]/0 to-[#FF6600]/0 group-hover:from-[#FF6600]/5 group-hover:to-[#003366]/5 rounded-2xl transition-all duration-500"></div>

                {/* Icon container with animated background */}
                <div className="relative mb-6">
                  <div className="absolute inset-0 bg-[#FF6600]/10 rounded-full blur-xl group-hover:bg-[#FF6600]/20 group-hover:scale-150 transition-all duration-500"></div>
                  <div className="relative w-20 h-20 bg-gradient-to-br from-[#FF6600]/10 to-[#FF6600]/5 rounded-full flex items-center justify-center mx-auto group-hover:scale-110 group-hover:rotate-6 transition-all duration-500">
                    <Icon className="text-[#FF6600] group-hover:text-[#FF6600] transition-all duration-500" size={36} />
                  </div>
                </div>

                <h3 className="text-xl font-bold text-[#003366] mb-3 text-center group-hover:text-[#FF6600] transition-colors duration-300">
                  {feature.title}
                </h3>
                <p className="text-gray-600 text-center leading-relaxed">{feature.description}</p>

                {/* Decorative underline on hover */}
                <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-0 h-1 bg-gradient-to-r from-[#FF6600] to-[#FF8533] group-hover:w-3/4 transition-all duration-500 rounded-full"></div>
              </div>
            );
          })}
        </div>

        {/* Story Content Section */}
        <div className={`mt-16 grid grid-cols-1 md:grid-cols-2 gap-12 items-center transition-all duration-500 delay-200 ${
          isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'
        }`}>
          <div className="relative group">
            {/* Image container with animated border */}
            <div className="absolute inset-0 bg-gradient-to-r from-[#FF6600] via-[#FF8533] to-[#FF6600] rounded-2xl opacity-0 group-hover:opacity-20 blur-xl transition-all duration-500 transform group-hover:scale-110"></div>
            <div className="relative overflow-hidden rounded-2xl shadow-2xl">
              <img
                src="https://images.pexels.com/photos/566345/pexels-photo-566345.jpeg"
                alt="Fresh Seafood"
                className="w-full h-[400px] object-cover transform group-hover:scale-110 transition-transform duration-700"
              />
              <div className="absolute inset-0 bg-gradient-to-t from-[#003366]/40 via-transparent to-transparent"></div>

              {/* Floating badge */}
              <div className="absolute top-6 right-6 bg-white/90 backdrop-blur-sm px-4 py-2 rounded-full shadow-lg transform group-hover:scale-110 group-hover:rotate-3 transition-all duration-500">
                <span className="text-[#FF6600] font-bold text-sm">Fresh Daily</span>
              </div>
            </div>
          </div>

          <div className="space-y-6">
            <div className="inline-block">
              <span className="text-[#FF6600] font-semibold text-sm uppercase tracking-wider">Our Journey</span>
            </div>
            <h3 className="text-3xl md:text-4xl font-bold text-[#003366] leading-tight">
              From Ocean to Your <span className="text-[#FF6600]">Table</span>
            </h3>

            <div className="space-y-4">
              <p className="text-gray-700 leading-relaxed text-lg">
                Our journey begins before sunrise, when our fishermen set sail into the Arabian Sea. 
                With decades of experience and deep respect for the ocean, they ensure only the finest 
                catch makes it to our facility.
              </p>
              <p className="text-gray-700 leading-relaxed text-lg">
                Once ashore, each fish undergoes rigorous quality checks and is processed in our 
                state-of-the-art hygienic facility. We maintain the cold chain from catch to delivery, 
                ensuring you receive seafood that's as fresh as it gets.
              </p>
            </div>

            {/* Stats or highlights */}
            <div className="grid grid-cols-2 gap-4 pt-6">
              <div className="text-center p-4 bg-white/50 backdrop-blur-sm rounded-xl border border-[#FF6600]/10">
                <div className="text-3xl font-bold text-[#FF6600] mb-1">50+</div>
                <div className="text-sm text-gray-600">Years Experience</div>
              </div>
              <div className="text-center p-4 bg-white/50 backdrop-blur-sm rounded-xl border border-[#FF6600]/10">
                <div className="text-3xl font-bold text-[#FF6600] mb-1">1000+</div>
                <div className="text-sm text-gray-600">Happy Customers</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AboutSection;
